<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Factories\HasFactory;use Illuminate\Database\Eloquent\Model;use Spatie\Permission\Traits\HasRoles;class Holiday extends Model{    use HasFactory ,HasRoles;    protected $fillable = [        'name',        'date',        'recurring',    ];    protected $casts = [        'date' => 'date',        'recurring' => 'boolean',    ];}